import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { format } from 'date-fns';

const App = () => {
  const [latestRates, setLatestRates] = useState([]);
  const [historicalRates, setHistoricalRates] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    fetchLatestRates();
  }, []);

  const fetchLatestRates = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/latest-rates');
      setLatestRates(response.data);
    } catch (error) {
      console.error('Error fetching latest rates:', error);
    }
  };

  const fetchHistoricalRates = async (date) => {
    try {
      const formattedDate = format(date, 'yyyy-MM-dd');
      const response = await axios.get(`http://localhost:3001/api/historical-rates/${formattedDate}`);
      setHistoricalRates(response.data);
    } catch (error) {
      console.error('Error fetching historical rates:', error);
    }
  };
  const formatDate = (date) => {
    return format(new Date(date), 'dd-MM-yyyy');
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    fetchHistoricalRates(date);
  };

  return (
    <div>
      <h1>Latest Exchange Rates</h1>
      {latestRates.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Currency Code</th>
              <th>Currency Name</th>
              <th>Rate</th>
              <th>Effective Date</th>
            </tr>
          </thead>
          <tbody>
            {latestRates.map((rate,index) => (
              <tr key={index}>
                <td>{rate.code}</td>
                <td>{rate.name}</td>
                <td>{rate.rate}</td>
                <td>{format(rate.effective_date,'yyyy-MM-dd')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Loading latest exchange rates...</p>
      )}

      <h2>Historical Exchange Rates</h2>
      <input type="date" value={format(selectedDate, 'yyyy-MM-dd')} onChange={(e) => handleDateChange(new Date(e.target.value))} />

      {historicalRates.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Currency Code</th>
              <th>Currency Name</th>
              <th>Rate</th>
              <th>Effective Date</th>
            </tr>
          </thead>
          <tbody>
            {historicalRates.map((rate) => (
              <tr key={rate.code}>
                <td>{rate.code}</td>
                <td>{rate.name}</td>
                <td>{rate.rate}</td>
                <td>{formatDate(rate.effective_date)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No historical rates available for the selected date.</p>
      )}
    </div>
  );
};

export default App;
