import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { format } from 'date-fns';

const App = () => {
  const [latestRates, setLatestRates] = useState([]);
  const [historicalRates, setHistoricalRates] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    fetchLatestRates();
  }, []);

  const fetchLatestRates = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/latest-rates');
      setLatestRates(response.data);
    } catch (error) {
      console.error('Error fetching latest rates:', error);
    }
  };

  const fetchHistoricalRates = async (date) => {
    try {
      const formattedDate = format(date, 'yyyy-MM-dd');
      const response = await axios.get(`http://localhost:3001/api/historical-rates/${formattedDate}`);
      setHistoricalRates(response.data);
    } catch (error) {
      console.error('Error fetching historical rates:', error);
    }
  };
  const formatDate = (date) => {
    return format(new Date(date), 'dd-MM-yyyy');
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    fetchHistoricalRates(date);
  };

  return (
    <div className="container">
      <div>
          <h1>Yet Another Forex</h1>
          <p><span style="font-family:Calibri; font-size:14pt">Rates</span><span> </span><span>as</span><span> </span><span>of</span><span> </span><span>31-03-2021</span><span> </span><span style="font-family:Calibri">31-03-2021</span><span style="font-family:Calibri; letter-spacing:26pt"> </span><span style="font-family:Calibri">im</span></p>
      </div>

    </div>
  );
};

export default App;
